            $description .= '</a>';
            $description .= '('.date("m/d/Y", strtotime($bill->bill_date)).')';

            if ($search !== "") {
                if (stripos($bill->bill_no, $search) !== false) {
                    $data[] = [
                        'id' => $bill->id,
                        'description' => $description,
                        'due_date' => date("m/d/Y", strtotime($bill->due_date)),
