            }

            $this->update_categories('Vendor Credit', $vendorCreditId, $data);
            $this->update_items('Vendor Credit', $vendorCreditId, $data);
        }

        return [
            'data' => $vendorCreditId,
            'success' => $update ? true : false,
            'message' => $update ? 'Update Successful!' : 'An unexpected error occured'
        ];
    }

