                                                $newBalance = floatval(str_replace(',', '', $incomeAcc->balance)) + floatval(str_replace(',', '', $totalAmount));
                                            } else {
                                                $newBalance = floatval(str_replace(',', '', $incomeAcc->balance)) - floatval(str_replace(',', '', $totalAmount));
                                            }
                                        break;
                                        case 'Sales Receipt' :
                                            if ($incomeAccType->account_name === 'Credit Card') {
                                                $newBalance = floatval(str_replace(',', '', $incomeAcc->balance)) + floatval(str_replace(',', '', $totalAmount));
                                            } else {
                                                $newBalance = floatval(str_replace(',', '', $incomeAcc->balance)) - floatval(str_replace(',', '', $totalAmount));
                                            }
                                        break;
                                        case 'Refund Receipt' :
