.modal {
  position: fixed;
  overflow: hidden;
  /* left: 0; */
  bottom: 0;
  width: 500px;
  height: auto;
  background-color: white;
  color: black;
  transition: opacity 0.4s, bottom 0.4s;  /* animate these properties */
  opacity: 1;                             /* open state is shown */
 
}

.modal.closed {
  opacity: 0;     /* close state is hidden and off screen */
  bottom: -300px;
}

.close {
  color: white;
  float: right;
  font-size: 28px;
  font-weight: bold;
  margin-right:2%;
  padding:1%;
  cursor: pointer;
}

